aws ec2 describe-volumes --query 'Volumes[*].[Attachments[0].InstanceId, VolumeId, Size]' --output text | grep 300 | awk '{print "aws cloudwatch put-metric-alarm --alarm-name  aws-ec2-" $1 "-" $2 "-Burst-Balance --alarm-description \"Alarm when Burst Balance less than 85 percent\" --metric-name  BurstBalance --namespace AWS/EBS --statistic Average --period 300 --threshold 85  --comparison-operator LessThanThreshold  --dimensions \"Name=VolumeId,Value=" $2 "\" --evaluation-periods 1 --alarm-actions arn:aws:sns:us-east-1:732800507661:aws-rds-alerting-all-pagerduty-integration --unit Percent"}' >> cloudwatch-ebs-alarms-all.sh
